import React, { Component } from 'react'
import {useStoreState} from 'easy-peasy'

class EmailConfirm extends Component {
    constructor() {
        super();
    }

    render(){
        const isLoading = useStoreState(State => State.auth.isLoading)
        return(
            <>
            {
                isLoading ?
                 <p> Its here</p>
                 :
                 <p>Here it is</p>
            }
            </>
        )
    }
}

export default EmailConfirm
