import React, { Component , useEffect} from 'react'
import {useStoreState} from 'easy-peasy'
import { Result, Button, Spin } from "antd";

function EmailConfirm(props) {
    const {isLoading, requestResponse} = useStoreState(State => State.auth);
    const {confirmEmail} = useStoreState(Actions => Actions.auth.confirmEmail);
    const { match: { params } } =props;
    useEffect(() => {
        confirmEmail(params.token)
    }, [])
    return (
        <>
            {
                isLoading ?       
                    requestResponse !== null ?
                        requestResponse.type === 'success' ?
                            <Result
                            status="success"
                            title="Email Verification"
                            subTitle={requestResponse.msg}
                            extra={[
                            <Button type="primary" key="console">
                                <Link to="/login">Login</Link>
                            </Button>
                            ]}
                        />
                        :
                            <Result
                            status="warning"
                            title="Email Verification"
                            subTitle={requestResponse.msg}
                            extra={[
                            <Button type="primary" key="console">
                                <Link to="/login">Home</Link>
                            </Button>
                            ]}
                        />

                :
                null                          
                :
                <Spin className='make-center' tip="Loading..."/>     
            }
        </>
    )
}

export default EmailConfirm
