import axios from "axios";
import { baseUrl, token } from "../configs/index";

const appHeader = header();
class HttpService {
  postData = async (item, url) => {
    return axios({
      method: "post",
      url: baseUrl + url,
      data: item,
    })
      .then((res) => res)
      .catch((error) => error.response.data);
  };

  getData = async (url) => {
    axios.get(baseUrl + url, { headers: { Authorization: token } })
    .then(response => {
        // If request is good...
        console.log(response.data);
      })
    .catch((error) => {
        console.log('error ' + error);
      });

  };
}
export default HttpService;
